import card from '../UI/Card';
import classes from './ProductItem.module.css';
import { useDispatch } from 'react-redux';
import { cartActions } from '../../store/cart-slice';
// import Button from 'react-bootstrap/Button';
// import 'bootstrap/dist/css/bootstrap.min.css';
// import Card from 'react-bootstrap/Card';






const ProductItem = (props) => {
  const { title, price, id, image } = props;
  const dispatch = useDispatch();

  const addToCartHandler = ()=> {
    dispatch(cartActions.addItemsToCart({
      id,
      title,
      price,
    }));
  };


  return (
    <div style={{display: 'flex', flexDirection: 'row'}}>
    <li >
      {/* className={classes.item} */}
      <Card className='e-card-horizontal' style={{ width: '15rem'}}>
      <Card.Body>
        <header>

        <Card.Title>{title}</Card.Title>
        <br/>
        <Card.Img variant="top" src={image} width="50%" height="50%"/>
        <br/>
        <br/>

          {/* <img src={image} width="50%" height="50%"/> */}
          <div className={classes.price}>INR {price.toFixed(2)}</div>
        </header>
        {/* <p>{description}</p> */}
        <br/>
        <div>
          <Button variant="success" onClick={addToCartHandler} >Add to Cart</Button>
        </div>
        </Card.Body>
      </Card>

    </li>
    </div>
  );
};

export default ProductItem;

